jQuery(document).ready(function($) {

    $('#top-search a').click(function(e) {
        e.preventDefault();
        $('.show-search').slideToggle('fast');
    });
    $('.to-top').click(function() {
        $('html, body').animate({
            scrollTop: 0
        }, 800);
        return false;
    });
    $('.bxslider').bxSlider({
        adaptiveHeight: true,
        mode: 'fade',
        pager: false,
        touchEnabled: true,
        captions: true
    });
    $(document).ready(function() {
        $(".container").fitVids();
    });
    $('.menu').slicknav({
        prependTo: '.menu-mobile',
